package cmpe113;


public class Break_for_switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i < 5; i++)
		{
			System.out.print(i + " ");
			
			switch (i) {
				case 1:
					System.out.println("One");
					break;
				case 2:
					System.out.println("One");
					break;
			}
		}		
		
	}

}
