package cmpe113;

public class SwitchQCT {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int code = 2;
		
		switch (code) {
		case 1:
			System.out.println("Hello.");
		case 3:
			System.out.println("Good-bye.");
			break;
		default:
			System.out.println("Till we meet again.");
			break;
		}

	}

}
